package utils;

public class Product {

	private String name;
	private int cost;
	
	public Product(String name, int cost) {

		this.name = name;
		this.cost = cost;
	}

	public String getName() {
		// TODO Auto-generated method stub
		return name;
	}

	public int getPrice() {
		// TODO Auto-generated method stub
		return cost;
	}

}
